import java.util.ArrayList;
public class MyClass {
    public static void main(String args[]) 
    {
        String pattern = "CGTGGCTCG";
        String genome = "";
        ArrayList positions = patternMatching(pattern, genome);
        for(int i = 0; i < positions.size() - 1; i++)
        {
            System.out.print(positions.get(i) + " ");
        }
        System.out.print(positions.get(positions.size() - 1));        
    }

    public static ArrayList patternMatching(String pattern, String genome)
    {
        ArrayList positions = new ArrayList<Integer>();
        for(int i = 0; i < (genome.length() - pattern.length() + 1); i++)
        {
            if((genome.substring(i, i + pattern.length())).compareTo(pattern) == 0)
            {
                positions.add(i);
            }
        }
        return positions;
    }
}